function ispisiSvePorudzbine(){$.ajax({url:"../../modules/orders/svePorudzbine.php",method:"post",success:function(t){$("#tabelaZaPorudzbine").html(vratiSvePorudzbineTabela(t)),$(".obrisiPor").click(obrisiPoruzdbinu),$(".detaljnijePor").click(detaljiJednePorudzbine)},error:function(t,e,a){console.log(t.status+e)}})}function vratiSvePorudzbineTabela(t){let e=1,a='<table class="korpa table table-striped" border="1">\n                    <thead class="thead-dark">\n                        <tr>\n                            <th>RB</th>\n                            <th>Ime</th>\n                            <th>Prezime</th>\n                            <th>Datum</th>\n                            <th>Obrisi</th>\n                            <th>Detaljnije</th>\n                        </tr>\n                    </thead>\n                    <tbody>';for(let n of t)a+=`<tr>\n                                <td>${e++}</td>\n                                <td>${n.ime}</td>\n                                <td>${n.prezime}</td>\n                                <td>${n.datumVreme_kupovine}</td>\n                                <td>\n                                    <a href='#' data-id="${n.idKupovine}" class='btn btn-primary obrisiPor'><i class="fas fa-trash-alt"></i></a>\n                                </td>\n                                <td>\n                                    <a href='#' data-id="${n.idKupovine}" class='btn btn-primary detaljnijePor'><i class="fas fa-info-circle"></i></a>\n                                </td>\n                            </tr>`;return a+="</tbody>\n                            </table>"}function obrisiPoruzdbinu(t){t.preventDefault();let e=$(this).data("id");$.ajax({url:"http://localhost/sajtphp1/modules/orders/obrisi.php",dataType:"json",method:"post",data:{id:e},success:function(t){ispisiSvePorudzbine()},error:function(t,e,a){console.log(t.status+e)}})}function detaljiJednePorudzbine(t){t.preventDefault();let e=$(this).data("id");$.ajax({url:"http://localhost/sajtphp1/modules/orders/detaljno.php",dataType:"json",method:"post",data:{id:e},success:function(t){$("#podaciPorudbine").hide(),$("#podaciPorudbine").html(detaljiJednePorudzbineTabela(t)),$("#podaciPorudbine").slideDown("slow"),$("#sakriDP").click(function(){$("#podaciPorudbine").slideUp("slow")})},error:function(t,e,a){console.log(t.status+e)}})}function detaljiJednePorudzbineTabela(t){let e=`<table class="korpa table table-striped" border="1" id="tabelaDP">\n                    <thead class="thead-dark">\n                        <tr>\n                            <td colspan="3">${t[0].ime} ${t[0].prezime}, ${t[0].email}</td>\n                        </tr>\n                        <tr>\n                            <td colspan='2'>Proizvod</td>\n                            <td>Kolicina</td>\n                        </tr>\n                    </thead>\n                    <tbody>\n                    `;for(let a of t)e+=`<tr>\n                                    <td>${a.naziv}</td><td><img src="../../${a.mala_putanja}" alt="../../${a.mala_alt}" class="img-fluid"/></td>\n                                    <td>${a.kolicina_proizvoda}</td>\n                                </tr>`;return e+='</tbody></table>\n                            <button type="button" id="sakriDP" class="btn btnprimary">Sakri</button>'}$(document).ready(function(){ispisiSvePorudzbine()});